import { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Dropdown from 'react-bootstrap/Dropdown'


const ButtonTypeA = () => {

    const [label, setLabel] = useState('All Emitters')

    return (
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
            {label}
        </Dropdown.Toggle>

        <Dropdown.Menu>
            <Dropdown.Item onClick = { () => setLabel('All Emitters') } href="#/action-1">All Emitters</Dropdown.Item>
            <Dropdown.Item onClick = { () => setLabel('Suppliers') } href="#/action-2">Suppliers</Dropdown.Item>
            <Dropdown.Item onClick = { () => setLabel('CO2 Capture, Supply and Underground Injection') } href="#/action-3">CO2 Capture, Supply and Underground Injection</Dropdown.Item>
            <Dropdown.Item onClick = { () => setLabel('Geologic Sequestration of CO2 (RR)') } href="#/action-3">Geologic Sequestration of CO2 (RR)</Dropdown.Item>
        </Dropdown.Menu>
        </Dropdown>
    )

}

export default ButtonTypeA
