import React, { useState, useRef } from 'react'
import Button from 'react-bootstrap/Button'

import ButtonGroup from 'react-bootstrap/ButtonGroup'

import Overlay from 'react-bootstrap/Overlay'
import Popover from 'react-bootstrap/Popover'

import sectors from '../data/mainfilters/sectors'


const ButtonTypeC = ( chapters ) => {

    const [show, setShow] = useState(false);
  const [target, setTarget] = useState(null);
  const ref = useRef(null);

  const handleClick = (event) => {
    setShow(!show);
    setTarget(event.target);
  };

  const sectorsToColumns = [
    [0, 2],
    [2, 4],
    [4, 6],
    [6, 9]
  ]

  const addListItem = (child) => {
    if(childList) {
      return (
        <div className = "sectors__sublist">
          {
            child.map((a) => {
              sectorBlock(a.name, 'subitem')
            })
          }
        </div>
    )}
  }

  const sectorBlock = (name, itemType, child ) => {
    return (
      <div className={ 'sectors__' + itemType }>
        <input type="checkbox"/>
        <div className="sectors__titles"> { name } </div>
        { addListItem(child) }
      </div>
    )
  }

  const scatterSectors = () => {

    sectorsToColumns.map((key) => {
      const sliced = sectors.list.slice(key[0], key[1])
      return (
        <div className = "sectors__column">
          {
            sliced.map((a) => {
              return(
                sectorBlock(a.name, 'block', a.child)
              )
            }
)
          }
        </div>
      )
    })
  }

  return (
    <div ref={ref}>
      <Button onClick={handleClick}>Filter Sectors</Button>

      <Overlay
        show={show}
        target={target}
        placement="bottom"
        container={ref.current}
        containerPadding={20}
      >
        <Popover id="popover-contained">
          <Popover.Title as="h3">Sectors</Popover.Title>
          <Popover.Content>
            <div className = 'sectors'>
              <div className = 'sectors__top'>
                <Button className = 'sectors__buttons'>Select All</Button>
                <Button className = 'sectors__buttons'>Deselect All</Button>
              </div>

              <div className = 'sectors__columns'>

              </div>
            </div>
          </Popover.Content>
        </Popover>
      </Overlay>
    </div>
  );



}

export default ButtonTypeC
