import React, { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Dropdown from 'react-bootstrap/Dropdown'

import ButtonGroup from 'react-bootstrap/ButtonGroup'


const ButtonTypeA = ( chapters ) => {

    const _chapters = chapters.chapters

    const [label, setLabel] = useState(_chapters[0].name)

    return (
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
            {label}
        </Dropdown.Toggle>

        <Dropdown.Menu>
            { _chapters.map(key => {

                const _name = (key.child) ? <span style={{marginLeft: '20px'}}>{key.name}</span> : <b>{key.name}</b>

                let _button;

                const CustomToggle = React.forwardRef(({ children, onClick }, ref) => {
                    return (
                    <a
                      className="dropdown-item dropdown-toggle"
                      href=""
                      ref={ref}
                      onMouseEnter={(e) => onClick(e)}
                      onClick={(e) => {
                        e.preventDefault();
                        onClick(e);
                      }}
                    >
                      {children}
                    </a>
                  )
                    });


                if(key.list) {
                    _button =
                        <Dropdown
                            as={ButtonGroup}
                            key='right'
                            id={`dropdown-button-drop-right`}
                            drop='right'
                            variant="secondary"
                            title={`Drop right`}
                            style={{width: '100%'}}
                        >
                        <Dropdown.Toggle id="dropdown-basic" as = {CustomToggle}>
                            { key.name }
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            <Dropdown.Item eventKey="1">Action</Dropdown.Item>
                            <Dropdown.Item eventKey="2">Another action</Dropdown.Item>
                            <Dropdown.Item eventKey="3">Something else here</Dropdown.Item>
                            <Dropdown.Divider />
                            <Dropdown.Item eventKey="4">Separated link</Dropdown.Item>
                        </Dropdown.Menu>
                        </Dropdown>
                } else {
                    _button = _name
                }

                return (
                <Dropdown.Item onClick = { () => setLabel(key.name) } href="#/action-1">{ 
                        _button
                    }
                </Dropdown.Item>
                )
            })
         }
        </Dropdown.Menu>
        </Dropdown>
    )



}

export default ButtonTypeA
