import { useState } from 'react'
import Button from 'react-bootstrap/Button'
import Dropdown from 'react-bootstrap/Dropdown'


const ButtonTypeA = ( props ) => {


    const chapters = props.chapters

    const [label, setLabel] = useState(chapters[0].name)

    return (
        <Dropdown>
        <Dropdown.Toggle variant="success" id="dropdown-basic">
            {label}
        </Dropdown.Toggle>

        <Dropdown.Menu>
            { chapters.forEach(key => {
                return (
                <Dropdown.Item onClick = { () => setLabel(key.name) } href="#/action-1">{key.name}</Dropdown.Item>
                )
            }) }
        </Dropdown.Menu>
        </Dropdown>
    )


}

export default ButtonTypeA
